func:
var cantidad = context.get("cantidad") || 0;
var vivero   = context.get("vivero") || 0;
var padre    = context.get("padre") || 0;

switch (msg.topic){

    case "cantidad":
        cantidad = msg.payload;
        context.set("cantidad", cantidad);
        return;

    case "vivero":
        vivero = msg.payload;
        context.set("vivero", vivero);
        return;

    case "padre":
        padre = msg.payload;
        context.set("padre", padre);
        return;

    case "go":
        msg.payload = { cantidad: cantidad, vivero: vivero, padre: padre};
}

return msg;
